import java.util.*;class DaysToYearMonthDays{	public static void main(String args[]){				Scanner sc = new Scanner(System.in);		System.out.println("Enter the number of days ");		int d = sc.nextInt();		int years = 0;		int months = 0;		 int days = 0;		while(d>365)		{			years++;			d=d-365;		}		while(d>30)		{			months++;			d=d-30;		}		while(d>0)		{			days++;			d=d-1;		}		System.out.println("year= "+years+" month= "+months+" days = "+days);	}}