import java.util.*;class EmployeeSalary{	public static void main(String args[]){		Scanner sc = new Scanner(System.in);		System.out.println("Enter the Employee basic salary");		double basicSal = sc.nextDouble();		double HRA ;		double DA;				if (basicSal < 10000){			HRA = basicSal * (0.1);  			DA = basicSal * (0.9);  			 		}		else{			HRA = 2000;  			DA = basicSal * (0.98);   			 		}				double GS = basicSal + DA + HRA;				System.out.println("Gross salary is "+GS);	}}