import java.util.*;class SimpleInterest{	public static void main(String args[]){		Scanner sc = new Scanner(System.in);				System.out.println("Enter the Principle amount ");		int a = sc.nextInt();		System.out.println("Enter the Rate of Interest ");		double b = sc.nextDouble();		System.out.println("Enter the Time Period in year ");		double c = sc.nextDouble();				double sI = (a * b * c) / 100;				System.out.println("Simple Interest is : "+sI);	}}