import java.util.*;class SwapNumber{	public static void main(String args[]){		Scanner sc = new Scanner(System.in);		System.out.println("Enter the first number = ");		int a = sc.nextInt();		System.out.println("Enter the second number = ");		int b = sc.nextInt();		System.out.println("Numbers before swap "+a+" and "+b);		a = a+b;		b = a-b;		a = a-b;				System.out.println("Numbers after swap "+a+" and "+b);			}}